import os, math
with open(os.path.dirname(os.path.realpath(__file__)) + "\\input1.txt", "r") as f: lines = [line.strip() for line in f]
print(math.prod([sum(1 for i in range(int(race[0]) + 1) if i*(int(race[0])-i)>int(race[1])) for race in list(map(list, zip(*[l.split()[1:] for l in lines])))]),[sum(1 for i in range(int(race[0])+1) if i*(int(race[0])-i)>int(race[1])) for race in [["".join(d) for d in [l.split()[1:] for l in lines]]]][0])